#Message is encoded with a simple shift cipher (caesar cipher)
#All punctuation and spaces are removed.  All letters are uppercase.
message = ""

def decode(message):
  #initialize several variables for use in the function
  alphabet = "ABCDEFGHIJKLMNOPQRSTUVWXYZ"
  solved = False
  shift = 0

  #start looping until the message has been decoded
  while not solved:
    #setup a holder variable
    result = ""
    #loop through each letter in the message
    for letter in message:
      #get the new letter and add it to the result
      letter_index = alphabet.index(letter)
      new_letter = alphabet[(letter_index+shift)%26]
      result = result + new_letter
    #print out the results
    print(result)
    print(shift)

    #check if the message has been decoded
    if input("Message Solved? ") == "True":
        solved = True
    else:
        shift += 1
      
decode(message)